<script lang="ts">
	import HoboPicture from './HoboPicture.svelte';
	import HoboLinkedIn from './HoboLinkedIn.svelte';
	import { languageTag } from '$lib/paraglide/runtime';
	import HypeEnglish from '$lib/hype/en.json';
	import HypeDutch from '$lib/hype/nl.json';
	import HypeRomanian from '$lib/hype/ro.json';

	import * as m from '$lib/paraglide/messages';
	import HoboType from '$lib/features/typewriter/HoboType.svelte';
	import HoboWave from '$lib/features/typewriter/HoboWave.svelte';

	const hypeLanguageMap = {
		en: HypeEnglish,
		nl: HypeDutch,
		ro: HypeRomanian
	};

	const hypeMessages = hypeLanguageMap[languageTag()];

	function random(min: number, max: number) {
		return Math.floor(Math.random() * (max - min + 1)) + min;
	}

	const randomHype = hypeMessages[random(0, hypeMessages.length - 1)];

	let shouldPrintSlogan = $state(false);
</script>

<main>
	<section id="hobo-void">
		<HoboType text={randomHype.messages} onComplete={() => (shouldPrintSlogan = true)} />
		{#if shouldPrintSlogan}
			<HoboType text={[randomHype.slogan]} />
		{:else}
			<HoboWave />
		{/if}
	</section>

	<section id="hobo-founders">
		<div class="hobo-founder">
			<HoboPicture
				founder="vlad"
				placeholder="data:image/webp;base64,"
			/>
			<section class="hobo-founder-description">
				<h2>Vlad</h2>
				<div class="hobo-founder-tagline">
					<span>{m.founder_title_vlad()}</span>
					<HoboLinkedIn href="https://www.linkedin.com/in/vladjerca/"></HoboLinkedIn>
				</div>
				<p>{m.founder_description_vlad()}</p>
			</section>
		</div>

		<div class="hobo-founder">
			<HoboPicture
				founder="sefer"
				placeholder="data:image/webp;base64,"
			/>
			<section class="hobo-founder-description">
				<h2>Sefer</h2>
				<div class="hobo-founder-tagline">
					<HoboLinkedIn href="https://www.linkedin.com/in/sturan/"></HoboLinkedIn>
					<span>{m.founder_title_sefer()}</span>
				</div>
				<p>{m.founder_description_sefer()}</p>
			</section>
		</div>
	</section>
</main>

